import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import mqtt from 'mqtt';

const MQTT_BROKER = "mqtt://localhost";  // Connect to localhost directly via MQTT
const MQTT_PORT = 1883;  // Standard MQTT port
const MQTT_TOPIC = "gps/location";

function Map() {
  const mapRef = useRef(null);
  const markerRef = useRef(null);
  const [map, setMap] = useState(null);

  useEffect(() => {
    const jakartaCoords = [-6.222020, 106.8681690];

    // Initialize the map
    const mapInstance = L.map(mapRef.current, {
      center: jakartaCoords,
      zoom: 11,
      zoomControl: false
    });
    setMap(mapInstance);

    // Add dark mode tile layer
    L.tileLayer('https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png', {
      attribution: '&copy; OpenStreetMap contributors &copy; CARTO',
      subdomains: 'abcd',
      maxZoom: 20
    }).addTo(mapInstance);

    // Initialize marker at initial coordinates and store in ref
    const initialMarker = L.marker(jakartaCoords).addTo(mapInstance);
    markerRef.current = initialMarker;

    // Connect to MQTT broker directly
    const client = mqtt.connect(`${MQTT_BROKER}:${MQTT_PORT}`);

    client.on('connect', () => {
      console.log('Connected to MQTT broker');
      client.subscribe(MQTT_TOPIC);
    });

    client.on('message', (topic, message) => {
      if (topic === MQTT_TOPIC) {
        try {
          const locationData = JSON.parse(message.toString());
          const newPosition = [locationData.latitude, locationData.longitude];

          // Update marker position using ref
          if (markerRef.current) {
            markerRef.current.setLatLng(newPosition);
            mapInstance.setView(newPosition);
          }
        } catch (error) {
          console.error('Error processing MQTT message:', error);
        }
      }
    });

    L.control.zoom({ position: 'topright' }).addTo(mapInstance);

    // Cleanup function
    return () => {
      client.end();
      mapInstance.remove();
    };
  }, []);

  return <div ref={mapRef} className="map-container" style={{ height: '100vh', width: '100%' }} />;
}

export default Map;
