import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import * as turf from '@turf/turf';
import 'leaflet/dist/leaflet.css';
import mqtt from 'mqtt';

function Map() {
  const mapRef = useRef(null);
  const markerRef = useRef(null);
  const mapInstanceRef = useRef(null); // Ref to hold the map instance
  const [location, setLocation] = useState({ latitude: -6.2088, longitude: 106.8456 }); // Initial Jakarta coordinates

  useEffect(() => {
    // Jakarta coordinates
    const jakartaCoords = [-6.2088, 106.8456];

    // Initialize the map
    const map = L.map(mapRef.current, {
      center: jakartaCoords,
      zoom: 11,
      zoomControl: false,
    });

    // Store the map instance in the ref
    mapInstanceRef.current = map;

    // Add the dark mode tile layer
    L.tileLayer('https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>',
      subdomains: 'abcd',
      maxZoom: 20,
    }).addTo(map);

    // Create a circle around Jakarta using Turf.js
    const center = turf.point(jakartaCoords);
    const radius = 10; // 10 km radius
    const options = { steps: 64, units: 'kilometers' };
    const circle = turf.circle(center, radius, options);

    // Convert Turf circle to Leaflet layer and add to map
    L.geoJSON(circle, {
      style: {
        color: '#ffffff',
        weight: 2,
        opacity: 0.7,
        fillOpacity: 0.1,
      },
    }).addTo(map);

    // Add a marker for Jakarta city center
    const marker = L.marker(jakartaCoords).addTo(map)
      .bindPopup('Jakarta City Center')
      .openPopup();

    markerRef.current = marker;  // Store marker reference

    // MQTT setup
    const client = mqtt.connect('mqtt://localhost:1883'); // Connect using TCP

    client.on('connect', () => {
      console.log('Connected to MQTT Broker');
      client.subscribe('gps/location', (err) => {
        if (err) console.error('Subscription error:', err);
      });
    });

    client.on('message', (topic, message) => {
      const data = JSON.parse(message.toString());
      setLocation({ latitude: data.latitude, longitude: data.longitude });  // Update location state with new coordinates
    });

    // Cleanup function
    return () => {
      map.remove();
      client.end();
    };
  }, []);

  // Smoothly move the marker to the new location
  const moveMarker = (newLocation) => {
    if (markerRef.current && mapInstanceRef.current) {
      const latLng = L.latLng(newLocation.latitude, newLocation.longitude);
      const startLatLng = markerRef.current.getLatLng();

      // Calculate distance and step details for smooth movement
      const distance = mapInstanceRef.current.distance(startLatLng, latLng);
      const duration = 1000; // Duration of the move in milliseconds
      const stepTime = 50; // Time per step in milliseconds
      const steps = Math.floor(duration / stepTime);
      const stepLat = (latLng.lat - startLatLng.lat) / steps;
      const stepLng = (latLng.lng - startLatLng.lng) / steps;

      let stepCount = 0;

      const move = () => {
        if (stepCount < steps) {
          const newLat = startLatLng.lat + stepLat * stepCount;
          const newLng = startLatLng.lng + stepLng * stepCount;
          markerRef.current.setLatLng([newLat, newLng]);
          stepCount++;
          requestAnimationFrame(move); // Request the next frame
        } else {
          // Ensure marker ends at the exact new location
          markerRef.current.setLatLng(latLng);
          markerRef.current.bindPopup(`Location updated: [${newLocation.latitude}, ${newLocation.longitude}]`).openPopup();
        }
      };
      move();
    }
  };

  useEffect(() => {
    // Update marker location when `location` state changes
    if (location) {
      moveMarker(location);
    }
  }, [location]);

  return <div ref={mapRef} className="map-container" style={{ height: '100vh' }} />;
}

export default Map;
