import React, { useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

function LocationMap({ latitude, longitude, deviceId }) {
  useEffect(() => {
    // Inisialisasi peta
    const map = L.map('map').setView([parseFloat(latitude), parseFloat(longitude)], 13);

    // Tambahkan tile layer (OpenStreetMap)
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    }).addTo(map);

    // Tambahkan marker
    const marker = L.marker([parseFloat(latitude), parseFloat(longitude)])
      .addTo(map)
      .bindPopup(`
        <b>Device ID:</b> ${deviceId}<br>
        <b>Latitude:</b> ${latitude}<br>
        <b>Longitude:</b> ${longitude}
      `);

    // Cleanup function
    return () => {
      map.remove();
    };
  }, [latitude, longitude, deviceId]); // Dependencies untuk useEffect

  return <div id="map" style={{ height: '400px', width: '100%', borderRadius: '4px' }}></div>;
}

export default LocationMap;
