import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import '../styles/HistoryStyles.css';

function HistoryPage() {
  const [histories, setHistories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedDevice, setSelectedDevice] = useState('all');
  const [devices, setDevices] = useState([]);
  const mapRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const markersLayerRef = useRef(null);

  useEffect(() => {
    // Inisialisasi peta
    if (mapRef.current && !mapInstanceRef.current) {
      mapInstanceRef.current = L.map(mapRef.current).setView([-6.2088, 106.8456], 11);

      // Tambahkan tile layer
      L.tileLayer('https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
        maxZoom: 19
      }).addTo(mapInstanceRef.current);

      // Inisialisasi layer group untuk markers
      markersLayerRef.current = L.layerGroup().addTo(mapInstanceRef.current);
    }

    return () => {
      if (mapInstanceRef.current) {
        mapInstanceRef.current.remove();
        mapInstanceRef.current = null;
      }
    };
  }, []);

  useEffect(() => {
    fetchHistories();
    fetchDevices(); // Ambil daftar devices untuk filter
  }, []);

  const fetchDevices = async () => {
    try {
      const response = await axios.get('http://localhost:3013/api/devices');
      setDevices(response.data.data);
    } catch (err) {
      console.error('Error fetching devices:', err);
      toast.error('Failed to fetch devices');
    }
  };

  const fetchHistories = async () => {
    try {
      const response = await axios.get('http://localhost:3013/api/history');
      setHistories(response.data.data);
      setLoading(false);
    } catch (err) {
      console.error('Error fetching histories:', err);
      setError(err.message);
      toast.error('Failed to fetch history data');
      setLoading(false);
    }
  };

  const handleDeviceFilter = (e) => {
    setSelectedDevice(e.target.value);
  };

  const filteredHistories = selectedDevice === 'all'
    ? histories
    : histories.filter(history => history.device_id === selectedDevice);

  const formatDateTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('en-GB', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const handleViewLocation = (history) => {
    try {
      const lat = parseFloat(history.latitude);
      const lng = parseFloat(history.longitude);

      if (!mapInstanceRef.current) {
        console.error('Map instance not found');
        return;
      }

      // Clear existing markers
      if (markersLayerRef.current) {
        markersLayerRef.current.clearLayers();
      }

      // Create custom icon
      const customIcon = L.icon({
        iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
        shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.7/images/marker-shadow.png',
        iconSize: [25, 41],
        iconAnchor: [12, 41],
        popupAnchor: [1, -34],
        shadowSize: [41, 41]
      });

      // Add new marker
      const marker = L.marker([lat, lng], { icon: customIcon })
        .bindPopup(`
          <div style="min-width: 200px;">
            <h6 style="margin: 0 0 5px 0;">Device Info</h6>
            <p style="margin: 0;"><strong>Device ID:</strong> ${history.device_id}</p>
            <p style="margin: 0;"><strong>Speed:</strong> ${history.speed} km/h</p>
            <p style="margin: 0;"><strong>Time:</strong> ${formatDateTime(history.recorded_at)}</p>
            <p style="margin: 0;"><strong>Location:</strong> ${lat.toFixed(6)}, ${lng.toFixed(6)}</p>
          </div>
        `)
        .addTo(markersLayerRef.current);

      // Open popup
      marker.openPopup();

      // Pan to location with animation
      mapInstanceRef.current.setView([lat, lng], 15, {
        animate: true,
        duration: 1
      });

      toast.success('Location displayed on map');
    } catch (err) {
      console.error('Error displaying location:', err);
      toast.error('Failed to display location on map');
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="history-page">
      <h1>Travel History</h1>

      {/* Map Container */}
      <div className="map-container mb-4">
        <div 
          ref={mapRef} 
          style={{ 
            height: '400px', 
            width: '100%', 
            borderRadius: '4px',
            backgroundColor: '#1e1e1e' 
          }}
        ></div>
      </div>

      {/* Device Filter */}
      <div className="filters mb-3">
        <select 
          className="form-select"
          value={selectedDevice}
          onChange={(e) => setSelectedDevice(e.target.value)}
        >
          <option value="all">All Devices</option>
          {devices.map(device => (
            <option key={device.device_id} value={device.device_id}>
              {device.device_name} ({device.device_id})
            </option>
          ))}
        </select>
      </div>

      {/* History Table */}
      <div className="table-responsive">
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Device ID</th>
              <th>Latitude</th>
              <th>Longitude</th>
              <th>Speed (km/h)</th>
              <th>Recorded At</th>
              <th>Created At</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredHistories.map((history) => (
              <tr key={history.history_id}>
                <td>{history.device_id}</td>
                <td>{Number(history.latitude).toFixed(6)}</td>
                <td>{Number(history.longitude).toFixed(6)}</td>
                <td>{history.speed}</td>
                <td>{formatDateTime(history.recorded_at)}</td>
                <td>{formatDateTime(history.created_at)}</td>
                <td>
                  <button 
                    className="btn btn-sm btn-info"
                    onClick={() => handleViewLocation(history)}
                  >
                    View on Map
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default HistoryPage;
