import React, { useEffect } from 'react';
import { useLocation } from '../context/LocationContext';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

function MapPage() {
  const { selectedLocation } = useLocation();

  useEffect(() => {
    // Inisialisasi peta
    const map = L.map('map').setView([0, 0], 13);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    }).addTo(map);

    // Jika ada lokasi yang dipilih, tampilkan marker dan center map ke lokasi tersebut
    if (selectedLocation) {
      const { latitude, longitude, deviceId, speed, recordedAt } = selectedLocation;
      const latLng = [parseFloat(latitude), parseFloat(longitude)];

      // Center map ke lokasi yang dipilih
      map.setView(latLng, 15);

      // Tambahkan marker dengan popup
      L.marker(latLng)
        .addTo(map)
        .bindPopup(`
          <b>Device ID:</b> ${deviceId}<br>
          <b>Speed:</b> ${speed} km/h<br>
          <b>Recorded At:</b> ${new Date(recordedAt).toLocaleString()}<br>
          <b>Location:</b> ${latitude}, ${longitude}
        `)
        .openPopup();
    }

    return () => {
      map.remove();
    };
  }, [selectedLocation]);

  return <div id="map" style={{ height: '100vh', width: '100%' }}></div>;
}

export default MapPage;
