import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import * as turf from '@turf/turf';
import 'leaflet/dist/leaflet.css';
import mqtt from 'mqtt';

function Map() {
  const mapRef = useRef(null);
  const [marker, setMarker] = useState(null);
  const [map, setMap] = useState(null);

  useEffect(() => {
    // Jakarta coordinates
    const jakartaCoords = [-6.2088, 106.8456];

    // Initialize the map
    const mapInstance = L.map(mapRef.current, {
      center: jakartaCoords,
      zoom: 11,
      zoomControl: false
    });
    setMap(mapInstance);

    // Add the dark mode tile layer
    L.tileLayer('https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>',
      subdomains: 'abcd',
      maxZoom: 20
    }).addTo(mapInstance);

    // Initialize marker
    const initialMarker = L.marker(jakartaCoords).addTo(mapInstance);
    setMarker(initialMarker);

    // Connect to MQTT broker
    const client = mqtt.connect('ws://localhost:9001');

    client.on('connect', () => {
      console.log('Connected to MQTT broker');
      client.subscribe('gps/location');
    });

    client.on('message', (topic, message) => {
      if (topic === 'gps/location') {
        try {
          const locationData = JSON.parse(message.toString());
          const newPosition = [locationData.latitude, locationData.longitude];

          // Update marker position
          if (marker) {
            marker.setLatLng(newPosition);
          }

          // Optional: Center map on new position
          mapInstance.setView(newPosition);
        } catch (error) {
          console.error('Error processing MQTT message:', error);
        }
      }
    });

    // Add zoom control to a custom position
    L.control.zoom({
      position: 'topright'
    }).addTo(mapInstance);

    // Cleanup function
    return () => {
      client.end();
      mapInstance.remove();
    };
  }, []);

  return <div ref={mapRef} className="map-container" />;
}

export default Map;
