import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import * as turf from '@turf/turf';
import 'leaflet/dist/leaflet.css';
import mqtt from 'mqtt';

function Map() {
  const mapRef = useRef(null);
  const [position, setPosition] = useState([-6.2088, 106.8456]); // Default ke Jakarta
  const [map, setMap] = useState(null);
  const [marker, setMarker] = useState(null);

  useEffect(() => {
    // Inisialisasi peta
    const mapInstance = L.map(mapRef.current).setView(position, 13);
    setMap(mapInstance);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(mapInstance);

    // Buat marker
    const newMarker = L.marker(position).addTo(mapInstance);
    setMarker(newMarker);

    // Koneksi ke MQTT
    const client = mqtt.connect('ws://localhost:9001');

    client.on('connect', () => {
      console.log('Terhubung ke MQTT broker');
      client.subscribe('gps/location', (err) => {
        if (err) {
          console.error('Error subscribe:', err);
        } else {
          console.log('Berhasil subscribe ke gps/location');
        }
      });
    });

    client.on('message', (topic, message) => {
      try {
        const data = JSON.parse(message.toString());
        console.log('Menerima lokasi baru:', data);

        // Update posisi marker dan peta
        const newPosition = [data.latitude, data.longitude];
        if (marker) {
          marker.setLatLng(newPosition);
        }
        mapInstance.setView(newPosition);
        setPosition(newPosition);

      } catch (error) {
        console.error('Error memproses pesan:', error);
      }
    });

    return () => {
      client.end();
      mapInstance.remove();
    };
  }, []);

  return (
    <div ref={mapRef} style={{ height: '100vh', width: '100%' }} />
  );
}

export default Map;
