import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import * as turf from '@turf/turf';
import 'leaflet/dist/leaflet.css';

function Map() {
  const mapRef = useRef(null);
  const [mapStyle, setMapStyle] = useState('dark');

  useEffect(() => {
    // Jakarta coordinates
    const jakartaCoords = [-6.2088, 106.8456];

    // Initialize the map
    const map = L.map(mapRef.current, {
      center: jakartaCoords,
      zoom: 11,
      zoomControl: false // Remove default zoom control
    });

    // Tile layer options
    const tileLayers = {
      dark: L.tileLayer('https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>',
        subdomains: 'abcd',
        maxZoom: 20
      }),
      vector: L.tileLayer('https://stamen-tiles-{s}.a.ssl.fastly.net/toner/{z}/{x}/{y}{r}.png', {
        attribution: 'Map tiles by <a href="http://stamen.com">Stamen Design</a>, <a href="http://creativecommons.org/licenses/by/3.0">CC BY 3.0</a> &mdash; Map data &copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
        subdomains: 'abcd',
        minZoom: 0,
        maxZoom: 20
      }),
      vectorial: L.tileLayer('https://{s}.tile.openstreetmap.fr/osmfr/{z}/{x}/{y}.png', {
        maxZoom: 20,
        attribution: '&copy; OpenStreetMap France | &copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      })
    };

    // Add the selected tile layer to the map
    tileLayers[mapStyle].addTo(map);

    // Create a circle around Jakarta using Turf.js
    const center = turf.point(jakartaCoords);
    const radius = 10; // 10 km radius
    const options = { steps: 64, units: 'kilometers' };
    const circle = turf.circle(center, radius, options);

    // Convert Turf circle to Leaflet layer and add to map
    L.geoJSON(circle, {
      style: {
        color: mapStyle === 'dark' ? '#ffffff' : '#000000',
        weight: 2,
        opacity: 0.7,
        fillOpacity: 0.1
      }
    }).addTo(map);

    // Add a marker for Jakarta city center
    L.marker(jakartaCoords).addTo(map)
      .bindPopup('Jakarta City Center')
      .openPopup();

    // Add zoom control to a custom position
    L.control.zoom({
      position: 'topright'
    }).addTo(map);

    // Add layer control
    L.control.layers({
      'Dark': tileLayers.dark,
      'Vector': tileLayers.vector,
      'Vectorial': tileLayers.vectorial
    }, null, { position: 'bottomright' }).addTo(map);

    // Cleanup function
    return () => {
      map.remove();
    };
  }, [mapStyle]);

  return (
    <>
      <div ref={mapRef} className="map-container" />
      <div className="map-style-control">
        <button onClick={() => setMapStyle('dark')}>Dark</button>
        <button onClick={() => setMapStyle('vector')}>Vector</button>
        <button onClick={() => setMapStyle('vectorial')}>Vectorial</button>
      </div>
    </>
  );
}

export default Map;
