import { Icon } from 'leaflet';
import { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import mqtt from 'mqtt';

// Perbaikan icon marker
import markerIcon from 'leaflet/dist/images/marker-icon.png';
import markerIconShadow from 'leaflet/dist/images/marker-shadow.png';

const icon = new Icon({
  iconUrl: markerIcon,
  shadowUrl: markerIconShadow,
  iconSize: [25, 41],
  iconAnchor: [12, 41]
});

function Map() {
  const [position, setPosition] = useState([-6.200000, 106.816666]); // Default ke Jakarta

  useEffect(() => {
    // Konfigurasi MQTT client
    const client = mqtt.connect('mqtt://broker-address');

    client.on('connect', () => {
      client.subscribe('topic/location');
    });

    client.on('message', (topic, message) => {
      const locationData = JSON.parse(message.toString());
      setPosition([locationData.latitude, locationData.longitude]);
    });

    return () => {
      client.end();
    };
  }, []);

  return (
    <MapContainer 
      center={position} 
      zoom={13} 
      style={{ height: "100vh", width: "100%" }}
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <Marker position={position} icon={icon}>
        <Popup>
          Lokasi saat ini
        </Popup>
      </Marker>
    </MapContainer>
  );
}

export default Map;
