import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import mqtt from 'mqtt';

// Define the MQTT topic
const MQTT_TOPIC = "gps/location";

function Map() {
  const mapRef = useRef(null);
  const [marker, setMarker] = useState(null);
  const [map, setMap] = useState(null);

  useEffect(() => {
    // Jakarta coordinates as the initial center
    const jakartaCoords = [-6.2088, 106.8456];

    // Initialize the map
    const mapInstance = L.map(mapRef.current, {
      center: jakartaCoords,
      zoom: 11,
      zoomControl: false
    });
    setMap(mapInstance);

    // Add the dark mode tile layer
    L.tileLayer('https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png', {
      attribution: '&copy; OpenStreetMap contributors &copy; CARTO',
      subdomains: 'abcd',
      maxZoom: 20
    }).addTo(mapInstance);

    // Initialize marker and add it to the map
    const initialMarker = L.marker(jakartaCoords).addTo(mapInstance);
    setMarker(initialMarker);

    // Connect to MQTT broker
    const client = mqtt.connect('ws://localhost:9001');

    client.on('connect', () => {
      console.log('Connected to MQTT broker');
      client.subscribe(MQTT_TOPIC);
    });

    client.on('message', (topic, message) => {
      if (topic === MQTT_TOPIC) {
        try {
          const locationData = JSON.parse(message.toString());
          const newPosition = [locationData.latitude, locationData.longitude];

          // Update marker position
          if (marker) {
            marker.setLatLng(newPosition);
          }

          // Center map on new position
          mapInstance.setView(newPosition, mapInstance.getZoom());
        } catch (error) {
          console.error('Error processing MQTT message:', error);
        }
      }
    });

    // Add zoom control to a custom position
    L.control.zoom({ position: 'topright' }).addTo(mapInstance);

    // Cleanup function
    return () => {
      client.end();
      mapInstance.remove();
    };
  }, [marker]);

  return <div ref={mapRef} className="map-container" style={{ height: '100vh', width: '100%' }} />;
}

export default Map;
