import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import * as turf from '@turf/turf';
import * as mqtt from 'mqtt/dist/mqtt';
import 'leaflet/dist/leaflet.css';

function Map() {
  const mapRef = useRef(null); // Reference to the map container
  const mapInstance = useRef(null); // Reference to the Leaflet map instance
  const markersRef = useRef({}); // Reference to store markers by device ID

  useEffect(() => {
    // Coordinates for Jakarta as the center point
    const jakartaCoords = [-6.2088, 106.8456];

    // Initialize the Leaflet map
    const map = L.map(mapRef.current, {
      center: jakartaCoords,
      zoom: 11,
      zoomControl: false,
    });
    mapInstance.current = map;

    // Add a tile layer for the map
    L.tileLayer('https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>',
      subdomains: 'abcd',
      maxZoom: 20,
    }).addTo(map);

    // Create a circle around Jakarta with a radius of 10 km using Turf.js
    const center = turf.point(jakartaCoords);
    const radius = 10; // Radius in kilometers
    const options = { steps: 64, units: 'kilometers' };
    const circle = turf.circle(center, radius, options);

    // Convert the Turf circle to a Leaflet layer and add it to the map
    L.geoJSON(circle, {
      style: {
        color: '#ffffff',
        weight: 2,
        opacity: 0.7,
        fillOpacity: 0.1,
      },
    }).addTo(map);

    // Add a zoom control to the map
    L.control.zoom({
      position: 'topright',
    }).addTo(map);

    // Establish MQTT connection
    const client = mqtt.connect('mqtt://localhost:1883');

    client.on('connect', () => {
      console.log('Connected to MQTT broker');
      client.subscribe('gps/location');
    });

    // Handle incoming MQTT messages
    client.on('message', (topic, message) => {
      console.log(`Received message on topic: ${topic}`);
      console.log(`Raw message: ${message.toString()}`);

      try {
        const locationData = JSON.parse(message.toString());
        console.log('Parsed message:', locationData);

        const { device_id, latitude, longitude } = locationData;

        // Validasi data
        if (!device_id || !latitude || !longitude) {
          console.error('Incomplete data received:', locationData);
          return;
        }

        console.log(`Processing data for Device ID: ${device_id}`);
        console.log(`Latitude: ${latitude}, Longitude: ${longitude}`);

        // Update marker
        if (markersRef.current[device_id]) {
          console.log(`Updating marker for Device ID: ${device_id}`);
          markersRef.current[device_id].setLatLng([latitude, longitude]);
        } else {
          console.log(`Creating new marker for Device ID: ${device_id}`);
          const marker = L.marker([latitude, longitude])
            .bindPopup(`Device ID: ${device_id}`)
            .addTo(mapInstance.current);
          markersRef.current[device_id] = marker;
        }
      } catch (err) {
        console.error('Error processing MQTT message:', err);
      }
    });


    // Cleanup on component unmount
    return () => {
      map.remove(); // Remove the map instance
      client.end(); // Disconnect the MQTT client
    };
  }, []);

  // Render the map container
  return <div ref={mapRef} className="map-container" style={{ height: '100vh' }} />;
}

export default Map;
