import React from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import AddUserPage from './pages/AddUserPage';
import HomePage from './pages/HomePage';
import VehicleListPage from './pages/VehicleListPage';
import VehicleCalculationPage from './pages/VehicleCalculationPage';
import Header from './components/Layout/Header';
import Sidebar from './components/Layout/Sidebar';
import Footer from './components/Layout/Footer';
import './App.css';  // Tambahkan baris ini

function AppContent() {
  const location = useLocation();
  const isLoginPage = location.pathname === '/login';

  return (
    <div className={`app ${isLoginPage ? 'login-page' : ''}`}>
      {!isLoginPage && <Header />}
      <div className="d-flex">
        {!isLoginPage && <Sidebar />}
        <main className={isLoginPage ? 'w-100' : 'flex-grow-1 p-3'}>
          <Routes>
            <Route path="/login" element={<LoginPage />} />
            <Route path="/add-user" element={<AddUserPage />} />
            <Route path="/" element={<HomePage />} />
            <Route path="/vehicles" element={<VehicleListPage />} />
            <Route path="/calculations" element={<VehicleCalculationPage />} />
          </Routes>
        </main>
      </div>
      {!isLoginPage && <Footer />}
    </div>
  );
}

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
