import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import * as turf from '@turf/turf';
import mqtt from 'mqtt';
import 'leaflet/dist/leaflet.css';

function Map() {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [marker, setMarker] = useState(null);

  useEffect(() => {
    // Initialize the map
    const jakartaCoords = [-6.2088, 106.8456];
    const initializedMap = L.map(mapRef.current, {
      center: jakartaCoords,
      zoom: 11,
      zoomControl: false,
    });

    // Add the dark mode tile layer
    L.tileLayer('https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>',
      subdomains: 'abcd',
      maxZoom: 20,
    }).addTo(initializedMap);

    // Create a circle around Jakarta using Turf.js
    const center = turf.point(jakartaCoords);
    const radius = 10; // 10 km radius
    const options = { steps: 64, units: 'kilometers' };
    const circle = turf.circle(center, radius, options);

    // Convert Turf circle to Leaflet layer and add to map
    L.geoJSON(circle, {
      style: {
        color: '#ffffff',
        weight: 2,
        opacity: 0.7,
        fillOpacity: 0.1,
      },
    }).addTo(initializedMap);

    // Add a marker for Jakarta city center
    L.marker(jakartaCoords)
      .addTo(initializedMap)
      .bindPopup('Jakarta City Center')
      .openPopup();

    // Add zoom control to a custom position
    L.control.zoom({
      position: 'topright',
    }).addTo(initializedMap);

    setMap(initializedMap);

    // Connect to MQTT broker
    const client = mqtt.connect('mqtt://localhost', { port: 1883 });

    client.on('connect', () => {
      console.log('Connected to MQTT Broker');
      client.subscribe('gps/location', (err) => {
        if (err) {
          console.error('Failed to subscribe:', err);
        }
      });
    });

    client.on('message', (topic, message) => {
      const locationData = JSON.parse(message.toString());
      const { latitude, longitude, timestamp } = locationData;

      console.log(`Received location data: ${JSON.stringify(locationData)}`);

      // Format the timestamp as a readable date
      const date = new Date(timestamp * 1000).toLocaleString();

      // Remove previous marker if it exists
      if (marker) {
        map.removeLayer(marker);
      }

      // Add a new marker at the received location
      const newMarker = L.marker([latitude, longitude])
        .addTo(map)
        .bindPopup(`Location updated at: ${date}`)
        .openPopup();
      setMarker(newMarker);

      // Pan the map to the new marker position
      map.setView([latitude, longitude], 13);
    });

    // Cleanup function
    return () => {
      client.end(); // Disconnect from the MQTT broker
      initializedMap.remove(); // Remove the map instance
    };
  }, []); // Empty dependency array ensures this effect runs once

  return <div ref={mapRef} className="map-container" style={{ height: '100vh', width: '100%' }} />;
}

export default Map;
