import React, { useEffect, useRef, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import mqtt from 'mqtt';

function Map() {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [marker, setMarker] = useState(null);
  const [locationHistory, setLocationHistory] = useState([]);

  useEffect(() => {
    // Jakarta center coordinates
    const initialPosition = [-6.2088, 106.8456];

    // Initialize map
    const mapInstance = L.map(mapRef.current).setView(initialPosition, 12);

    // Add dark mode tile layer
    L.tileLayer('https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png', {
      attribution: '&copy; OpenStreetMap contributors &copy; CARTO',
      maxZoom: 19
    }).addTo(mapInstance);

    // Create marker with custom popup
    const markerInstance = L.marker(initialPosition)
      .bindPopup('Current Location')
      .addTo(mapInstance);

    setMap(mapInstance);
    setMarker(markerInstance);

    // Connect to MQTT broker
    console.log('Connecting to MQTT broker...');
    const client = mqtt.connect('ws://localhost:9001');

    client.on('connect', () => {
      console.log('Connected to MQTT broker');
      client.subscribe('gps/location');
    });

    client.on('message', (topic, message) => {
      try {
        const locationData = JSON.parse(message.toString());
        console.log('Received location:', locationData);

        const newPosition = [locationData.latitude, locationData.longitude];

        // Update marker position
        if (markerInstance) {
          markerInstance.setLatLng(newPosition);
          markerInstance.setPopupContent(`
            Lat: ${locationData.latitude.toFixed(4)}<br>
            Lng: ${locationData.longitude.toFixed(4)}<br>
            Time: ${new Date(locationData.timestamp * 1000).toLocaleTimeString()}
          `);
        }

        // Update map view to follow marker
        mapInstance.setView(newPosition);

        // Update location history
        setLocationHistory(prev => [...prev, newPosition]);

      } catch (error) {
        console.error('Error processing MQTT message:', error);
      }
    });

    // Cleanup on component unmount
    return () => {
      console.log('Cleaning up MQTT connection...');
      client.end();
      mapInstance.remove();
    };
  }, []);

  return (
    <div>
      <div 
        ref={mapRef} 
        style={{ height: '80vh', width: '100%' }}
      />
      <div style={{ padding: '10px', background: '#f5f5f5' }}>
        <h3>Location Updates</h3>
        {locationHistory.slice(-5).map((loc, index) => (
          <div key={index}>
            Lat: {loc[0].toFixed(4)}, Lng: {loc[1].toFixed(4)}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Map;
