function Map() {
  const mapRef = useRef(null);
  const currentMarkerRef = useRef(null);
  const initialMarkerRef = useRef(null);
  const polylineRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const initialCoordsRef = useRef(null);  // Add this to store initial coordinates

  useEffect(() => {
    if (mapInstanceRef.current) {
      mapInstanceRef.current.remove();
    }

    // Initialize the map with a default center (will be updated with first MQTT message)
    const map = L.map(mapRef.current, {
      center: [0, 0],  // Default center
      zoom: 11,
      zoomControl: false
    });
    mapInstanceRef.current = map;

    // Add the dark mode tile layer
    L.tileLayer('https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png', {
      attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors &copy; <a href="https://carto.com/attributions">CARTO</a>',
      subdomains: 'abcd',
      maxZoom: 20
    }).addTo(map);

    // Custom icons definition...
    const initialIcon = L.divIcon({
      className: 'custom-div-icon',
      html: `<div style='background-color: #4A90E2; width: 15px; height: 15px; border-radius: 50%; border: 2px solid white;'></div>`,
      iconSize: [15, 15],
      iconAnchor: [7, 7]
    });

    const currentIcon = L.divIcon({
      className: 'custom-div-icon',
      html: `<div style='background-color: #FF4444; width: 15px; height: 15px; border-radius: 50%; border: 2px solid white;'></div>`,
      iconSize: [15, 15],
      iconAnchor: [7, 7]
    });

    // Initialize polyline
    const polyline = L.polyline([], {
      color: '#FF4444',
      weight: 2,
      opacity: 0.7,
      dashArray: '5, 10'
    }).addTo(map);
    polylineRef.current = polyline;

    // MQTT Connection
    const client = mqtt.connect('mqtt://broker.hivemq.com:1883');
    const MQTT_TOPIC = 'gps/location';

    client.on('connect', () => {
      console.log('Connected to MQTT broker');
      client.subscribe(MQTT_TOPIC);
    });

    client.on('message', (topic, message) => {
      if (topic === MQTT_TOPIC) {
        try {
          const locationData = JSON.parse(message.toString());
          const newPosition = [locationData.latitude, locationData.longitude];

          // If this is the first position received, set it as initial position
          if (!initialCoordsRef.current) {
            initialCoordsRef.current = newPosition;

            // Add initial marker
            const initialMarker = L.marker(newPosition, { icon: initialIcon }).addTo(map);
            initialMarker.bindPopup('Initial Position').openPopup();
            initialMarkerRef.current = initialMarker;

            // Create circle around initial position
            const center = turf.point([locationData.longitude, locationData.latitude]);
            const radius = 10;
            const options = { steps: 64, units: 'kilometers' };
            const circle = turf.circle(center, radius, options);

            L.geoJSON(circle, {
              style: {
                color: '#ffffff',
                weight: 2,
                opacity: 0.7,
                fillOpacity: 0.1
              }
            }).addTo(map);

            // Center map on initial position
            map.setView(newPosition, 11);
          }

          // Update current position marker
          if (currentMarkerRef.current) {
            currentMarkerRef.current.remove();
          }

          const newMarker = L.marker(newPosition, { icon: currentIcon }).addTo(map);
          newMarker.bindPopup(`Current Position<br>Lat: ${locationData.latitude}<br>Lng: ${locationData.longitude}`).openPopup();
          currentMarkerRef.current = newMarker;

          // Update polyline if we have initial coordinates
          if (initialCoordsRef.current) {
            polylineRef.current.setLatLngs([initialCoordsRef.current, newPosition]);

            // Fit bounds to show both markers
            const bounds = L.latLngBounds([initialCoordsRef.current, newPosition]);
            map.fitBounds(bounds, { padding: [50, 50] });
          }

        } catch (error) {
          console.error('Error processing MQTT message:', error);
        }
      }
    });

    // Add zoom control
    L.control.zoom({
      position: 'topright'
    }).addTo(map);

    // Cleanup function
    return () => {
      if (mapInstanceRef.current) {
        mapInstanceRef.current.remove();
        mapInstanceRef.current = null;
      }
      if (currentMarkerRef.current) {
        currentMarkerRef.current = null;
      }
      if (initialMarkerRef.current) {
        initialMarkerRef.current = null;
      }
      if (client) {
        client.end();
      }
    };
  }, []);

  return (
    <div 
      ref={mapRef} 
      className="map-container" 
      style={{ height: '100vh', width: '100%' }} 
    />
  );
}
